import serverConfig from './server';

import localStorageKeys from './local-storage-keys';

export { serverConfig, localStorageKeys };

const localStorageKeys = {
  USER_DETAILS: 'userDetails',
};

Object.freeze(localStorageKeys);

export default localStorageKeys;

const server = {
  SERVER_BASE_URL: process.env.SERVER_BASE_URL,
};

Object.freeze(server);

export default server;

import {
  SET_CHANNELS,
  SET_USER,
  SET_CONNECTED,
  SET_ACTIVE_CHANNEL,
  SET_CHANNELS_MAP,
  LOGOUT_USER,
  ADD_NEW_MESSAGE,
  GENERATE_CHANNELS_MAP,
  GENERATE_USERS_MAP,
  SET_FIRST_USER_STATUS,
  SET_USER_ONLINE,
  SET_USER_OFFLINE,
} from './actions-types';
import { localStorageKeys } from '../../config';

const setChannelsHandler = dispatch => (channels) => {
  dispatch({
    type: SET_CHANNELS,
    payload: { channels },
  });
};

const setUserHandler = dispatch => (user) => {
  localStorage.setItem(localStorageKeys.USER_DETAILS, JSON.stringify(user));
  dispatch({
    type: SET_USER,
    payload: { user },
  });
};

const setConnectedHandler = dispatch => () => {
  dispatch({
    type: SET_CONNECTED,
  });
};

const setActiveChannelHandler = dispatch => (channelId) => {
  dispatch({
    type: SET_ACTIVE_CHANNEL,
    payload: channelId,
  });
};

const setChannelsMapHandler = dispatch => (messages, channelId) => {
  dispatch({
    type: SET_CHANNELS_MAP,
    payload: {
      messages,
      channelId,
    },
  });
};

const logoutAndResetHandler = dispatch => () => {
  localStorage.removeItem(localStorageKeys.USER_DETAILS);
  dispatch({
    type: LOGOUT_USER,
  });
};

const newMessageHandler = dispatch => (message) => {
  dispatch({
    type: ADD_NEW_MESSAGE,
    payload: {
      message,
      channelId: message.channelId,
    },
  });
};

const generateChannelsMapHandler = dispatch => (channels) => {
  dispatch({
    type: GENERATE_CHANNELS_MAP,
    payload: { channels },
  });
};

const generateUsersMapHandler = dispatch => (users) => {
  dispatch({
    type: GENERATE_USERS_MAP,
    payload: { users },
  });
};

const setFirstUserStatusHandler = dispatch => (onlineUserIds) => {
  dispatch({
    type: SET_FIRST_USER_STATUS,
    payload: onlineUserIds,
  });
};

const setUserOnlineHandler = dispatch => (userId) => {
  dispatch({
    type: SET_USER_ONLINE,
    payload: userId,
  });
};

const setUserOfflineHandler = dispatch => (userId) => {
  dispatch({
    type: SET_USER_OFFLINE,
    payload: userId,
  });
};

export {
  setChannelsHandler,
  setUserHandler,
  setConnectedHandler,
  logoutAndResetHandler,
  setActiveChannelHandler,
  setChannelsMapHandler,
  newMessageHandler,
  generateChannelsMapHandler,
  generateUsersMapHandler,
  setFirstUserStatusHandler,
  setUserOfflineHandler,
  setUserOnlineHandler,
};

export const SET_CHANNELS = 'SET_CHANNELS';
export const SET_USER = 'SET_USER';
export const SET_CONNECTED = 'SET_CONNECTED';
export const SET_ACTIVE_CHANNEL = 'SET_ACTIVE_CHANNEL';
export const SET_CHANNELS_MAP = 'SET_CHANNELS_MAP';
export const LOGOUT_USER = 'LOGOUT_USER';
export const ADD_NEW_MESSAGE = 'ADD_NEW_MESSAGE';
export const GENERATE_CHANNELS_MAP = 'GENERATE_CHANNELS_MAP';
export const GENERATE_USERS_MAP = 'GENERATE_USERS_MAP';
export const SET_FIRST_USER_STATUS = 'SET_FIRST_USER_STATUS';
export const SET_USER_ONLINE = 'SET_USER_ONLINE';
export const SET_USER_OFFLINE = 'SET_USER_OFFLINE';

import axios from 'axios';
import { serverConfig } from '../../config';

const fetchMessagesApi = setChannelsMap => async (channelId) => {
  const { data: messages } = await axios.get(`${serverConfig.SERVER_BASE_URL}/messages`, {
    params: { channelId },
  });
  setChannelsMap(messages, channelId);
};

const fetchChannelsApi = generateChannelsMap => async (userId) => {
  const { data: channels } = await axios.get(`${serverConfig.SERVER_BASE_URL}/channels`, {
    params: { users: userId },
  });
  generateChannelsMap(channels);
};

const fetchUsersApi = generateUsersMap => async () => {
  const { data: users } = await axios.get(`${serverConfig.SERVER_BASE_URL}/users`);
  generateUsersMap(users);
};

export { fetchChannelsApi, fetchMessagesApi, fetchUsersApi };

import { useReducer, useRef, useEffect } from 'react';
import createUseContext from 'constate';
import reducer from './reducer';
import getInitialState from './state';

import {
  setChannelsHandler,
  setUserHandler,
  setConnectedHandler,
  logoutAndResetHandler,
  setActiveChannelHandler,
  setChannelsMapHandler,
  newMessageHandler,
  generateChannelsMapHandler,
  generateUsersMapHandler,
  setFirstUserStatusHandler,
} from './action-handlers';

import {
  fetchMessagesApi,
  fetchChannelsApi,
  fetchUsersApi,
} from './api';

const initialState = getInitialState();

const useHome = () => {
  const socketMethods = useRef(null);
  const [state, dispatch] = useReducer(reducer, initialState);
  const {
    user,
    channels,
    isConnected,
    activeChannel,
    channelsMap,
    channelIds,
    allUserIds,
    allUsersMap,
  } = state;

  const generateChannelsMap = useRef(generateChannelsMapHandler(dispatch));
  const fetchChannels = useRef(
    fetchChannelsApi(generateChannelsMap.current),
  );
  const generateUsersMap = useRef(generateUsersMapHandler(dispatch));
  const fetchUsers = useRef(fetchUsersApi(generateUsersMap.current));
  const setChannels = useRef(setChannelsHandler(dispatch));
  const setUser = useRef(setUserHandler(dispatch));
  const setConnected = useRef(setConnectedHandler(dispatch));
  const logoutUser = useRef(logoutAndResetHandler(dispatch));
  const setActiveChannel = useRef(setActiveChannelHandler(dispatch));
  const setChannelsMap = useRef(setChannelsMapHandler(dispatch));
  const fetchMessages = useRef(fetchMessagesApi(setChannelsMap.current));
  const newMessage = useRef(newMessageHandler(dispatch));
  const setFirstUserStatus = useRef(setFirstUserStatusHandler(dispatch));
  const sendMessage = useRef((message, channelId) => {
    if (socketMethods) {
      socketMethods.current.sendMessage({ message, channelId });
    }
  });

  useEffect(() => {
    if (user && user.username) {
      if (!socketMethods.current) {
        //  TODO: connect with socket module
      }
    }
  }, [user, newMessage]);

  return {
    user,
    channels,
    isConnected,
    setChannels: setChannels.current,
    setUser: setUser.current,
    channelsMap,
    setConnected: setConnected.current,
    sendMessage: sendMessage.current,
    logoutUser: logoutUser.current,
    activeChannel,
    setActiveChannel: setActiveChannel.current,
    fetchMessages: fetchMessages.current,
    fetchChannels: fetchChannels.current,
    channelIds,
    allUserIds,
    allUsersMap,
    fetchUsers: fetchUsers.current,
    setFirstUserStatus: setFirstUserStatus.current,
  };
};

const useHomeContext = createUseContext(useHome);

export { useHomeContext, useHome };

import { localStorageKeys } from '../../config';

const getInitialState = () => {
  const user = localStorage.getItem(localStorageKeys.USER_DETAILS);

  const initialState = {
    user: user ? JSON.parse(user) : {},
    isConnecting: true,
    isConnected: false,
    channelIds: [],
    channelsMap: {},
    allUserIds: [],
    allUsersMap: {},
    activeChannel: null,
    activeUser: null,
    error: null,
  };

  return initialState;
};

export default getInitialState;

